import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [steamId, setSteamId] = useState('');
  const [profile, setProfile] = useState(null);

  const fetchProfile = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/user/${steamId}`);
      setProfile(response.data);
    } catch (error) {
      console.error('Error fetching profile:', error);
    }
  };

  return (
    <div className="App">
      <h1>Steam Profile Viewer</h1>
      <input
        type="text"
        placeholder="Enter Steam ID"
        value={steamId}
        onChange={(e) => setSteamId(e.target.value)}
      />
      <button onClick={fetchProfile}>Fetch Profile</button>
      {profile && (
        <div>
          <h2>{profile.personaname}</h2>
          <img src={profile.avatarfull} alt="Avatar" />
          <p>Profile URL: <a href={profile.profileurl}>{profile.profileurl}</a></p>
          <p>Status: {profile.personastate}</p>
        </div>
      )}
    </div>
  );
}

export default App;
