const express = require('express');
const axios = require('axios');
const cors = require('cors');
require('dotenv').config();

const app = express();
const PORT = process.env.PORT || 5000;
const STEAM_API_KEY = process.env.STEAM_API_KEY;

app.use(cors());

app.get('/api/user/:steamid', async (req, res) => {
  const { steamid } = req.params;
  try {
    const response = await axios.get(
      `http://api.steampowered.com/ISteamUser/GetPlayerSummaries/v2/`,
      {
        params: {
          key: "8CC50B5636A1675EFCB7A6EC699ADADC",
          steamids: steamid,
        },
      }
    );
    res.json(response.data.response.players[0]);
  } catch (error) {
    console.error(error);
    res.status(500).send('Error fetching Steam data');
  }
});

app.listen(PORT, () => console.log(`Server running on port ${PORT}`));
